import React, { useState, useEffect } from 'react';
import { book } from './Book.js';

const chapter = 52;
const speed = 200;
let i = 0;

function Reader() {
  const [currentChapter, setCurrentChapter] = useState(chapter);
  let words = book.chapter[currentChapter - 1].content.split(/\s/);
  let content = (
    <>
      <h5>
        { book.chapter[currentChapter - 1].title }
      </h5>
      <p>
        { words.length > i
        ? words[i] 
        : <button onClick={() => {setCurrentChapter(currentChapter + 1); i = 0}}>Next Chapter</button>}
      </p>
      <button onClick={() => { setCurrentChapter(currentChapter - 1); i = 0 }}>prev</button>
      <button onClick={() => { setCurrentChapter(currentChapter + 1); i = 0 }}>next</button>
    </>
  );
  useEffect(() => {
      const interval = setInterval(() => i += 1, speed);
  }, []);

  return content;
}

export default Reader;
