import React, { useState, useEffect } from 'react';
import { book } from './Book.js';
import moment from 'moment';

const chapter = 97;
const speed = 180;
let word = 0;
const words = book.chapter[chapter - 1].content.split(/\s/);

function Reader() {
  const [time, setTime] = useState(moment());
  const [initialTime, setInitialTime] = useState(moment());
  let content = (
    <>
      <h5>
        { book.chapter[chapter - 1].title }
      </h5>
      <p>
        { moment().valueOf() % speed }
      </p>
      <p>
        { words[word] }
      </p>
    </>
  );
  useEffect(() => {
    setInterval(() => setTime(moment()), 5);
    (moment().valueOf() % speed) === 0 ? (word = word + 1) : console.log();
  }, []);

  return content;
}

export default Reader;
