import React, { useState, useEffect } from 'react';
import { book } from './Book.js';

const chapter = 52;
const speed = 200;

function Reader() {
  const [index, setIndex] = useState(0);
  const [currentChapter, setCurrentChapter] = useState(chapter);
  let words = book.chapter[currentChapter - 1].content.split(/\s/);
  let content = (
    <>
      <h5>
        { book.chapter[currentChapter - 1].title }
      </h5>
      <p>
        { words.length > index
        ? words[index] 
        : <button onClick={() => {setCurrentChapter(currentChapter + 1); setIndex(0)}}>Next Chapter</button>}
      </p>
      <div>{index}</div>
      <button onClick={() => { setCurrentChapter(currentChapter - 1); setIndex(0) }}>prev</button>
      <button onClick={() => { setCurrentChapter(currentChapter + 1); setIndex(0) }}>next</button>
    </>
  );
  useEffect(() => {
      const interval = setInterval(() => index =  index + 1, 1000);
  }, [index]);

  return content;
}

export default Reader;
